mapper.select:
SELECT hr.`address`,hr.`enabled`,hr.`id`,hr.`name`,hr.`phone`,hr.`remark`,hr.`telephone`,hr.`userface`,hr.`username`, r.`id` AS rid,r.`name` AS rname,r.`nameZh`AS rnameZh FROM hr LEFT JOIN hr_role hrr ON hr.`id`=hrr.`hrid` LEFT JOIN role r ON hrr.`rid`=r.`id` WHERE hr.`id`!=#{hrid}
and hr.name like concat('%',#{keywords},'%')
order by hr.id
